import React, { useState } from 'react';
import FeedbackOptions from './FeedbackOptions/FeedbackOptions';
import Statistics from './Statistics/Statistics';
import Notification from './Notification/Notification';
import Section from './Section/Section';

export const App =()=>{


  const [feedbacks, setFeedbacks] = useState({good: 0,
    neutral: 0,
    bad: 0,});


    handleFeedback = event => {
        const feedback = event.target.name;
        setFeedbacks(prev => {
          return {
            [feedback]: prev[feedback] + 1,
          };
      });
    };


    countTotalFeedback = () => {
      let totalFeedback = feedbacks.good + feedbacks.neutral + feedbacks.bad;
      return totalFeedback;
    };

    countPositiveFeedbackPercentage = () => {
      return Math.round((feedbacks.good / countTotalFeedback()) * 100);
    };

   return (
      <>
        <Section title="Please leave feedback">
          <FeedbackOptions
            options={feedbacks}
            onLeaveFeedback={handleFeedback}
          />

          {this.countTotalFeedback() !== 0 ? (
            <Statistics
              good={this.state.good}
              neutral={this.state.neutral}
              bad={this.state.bad}
              total={this.countTotalFeedback()}
              positivePercentage={this.countPositiveFeedbackPercentage()}
            />
          ) : (
            <Notification message="There is no feedback" />
          )}
        </Section>
      </>
    );
  }
