import React, {useState} from 'react'
import style from "./FeedbackOptions.module.css"


// import PropTypes from 'prop-types';

export default function Feedback() {

const [data, setData] = useState({good:0, neutral:0, bad:0,});

const handlerData = () => {
 setData((prev) => [...prev, data])
}


  return (

        <div className={style.buttonContainer}>
        <button type="button" className={style.feedbackButton} onClick={handlerData}>Good</button>
        <button type="button" className={style.feedbackButton} onClick={handlerData}>Neutral</button>
        <button type="button" className={style.feedbackButton} onClick={handlerData}>Bad</button>
        </div>


  )
}
