import React, {useState} from 'react'
import style from "./Feedback.module.css"

export default function Feedback() {

const [data, setData] = useState({good:0, neutral:0, bad:0,});

const handlerData = () => {
 setData((prev) => [...prev, data])
}


  return (
    <div className={style.feedbackWrapper}>
        <h1>Please leave feedback</h1>
        <div className={style.buttonContainer}>
        <button className={style.feedbackButton} onClick={handlerData}>Good</button>
        <button className={style.feedbackButton} onClick={handlerData}>Neutral</button>
        <button className={style.feedbackButton} onClick={handlerData}>Bad</button>
        </div>
    </div>
  )
}
