import React, {useState} from 'react';
import style from './FeedbackOptions.module.css';

import PropTypes from 'prop-types';

export default function FeedbackOptions({ options, handleFeedback, countTotalFeedback, countPositiveFeedbackPercentage }) {
const [feedbacks, setFeedbacks] = useState({good: 0,
  neutral: 0,
  bad: 0,});


  handleFeedback = event => {
    const feedback = event.target.name;
    setFeedbacks(prev => {
      return {
        [feedback]: prev[feedback] + 1,
      };
    });
  };


  countTotalFeedback = () => {
    let totalFeedback = feedbacks.good + feedbacks.neutral + feedbacks.bad;
    return totalFeedback;
  };

  countPositiveFeedbackPercentage = () => {
    return Math.round((feedbacks.good / countTotalFeedback()) * 100);
  };

  return (
    <div className={style.buttonContainer}>
      {options.map(option => {
        return (
          <button
            key={option}
            type="button"
            name={option}
            onClick={handleFeedback}
            className={style.feedbackButton}
          >
            {option}
          </button>
        );
      })}
    </div>
  );
}

FeedbackOptions.propTypes = {
  options: PropTypes.array.isRequired,
  onLeaveFeedback: PropTypes.func.isRequired,
};
